import 'package:flutter/material.dart';class Widdddd extends StatefulWidget {  const Widdddd({Key? key}) : super(key: key);  @override  State<Widdddd> createState() => _WidddddState();}class _WidddddState extends State<Widdddd> {  @override  Widget build(BuildContext context) {    return Scaffold(      body:         CustomScrollView(          slivers: [            SliverPersistentHeader(              floating: false,              pinned: true,              delegate: PersistentHeader(),),            SliverList(              delegate: SliverChildBuilderDelegate(                    (_, int index) {                  return  Container(                    height: MediaQuery.of(context).size.height * .7,                    decoration: const BoxDecoration(                        color: Colors.red,                        borderRadius: BorderRadius.only(topLeft: Radius.circular(20), topRight: Radius.circular(20))),                  );                },                childCount: 1,              ),            ),          ],        )    );  }}class PersistentHeader extends SliverPersistentHeaderDelegate {  @override  Widget build(      BuildContext context, double shrinkOffset, bool overlapsContent) {    return SizedBox(      height: MediaQuery.of(context).size.height * .3,      child: ListView(        scrollDirection: Axis.horizontal,        children: List.generate(10, (index) => Container(          margin: const EdgeInsets.symmetric(horizontal: 20, vertical: 20),          color: Colors.greenAccent,          width: 100,          height: 50,        )),    ),);  }  @override  double get maxExtent => 200;  @override  double get minExtent => 200;  @override  bool shouldRebuild(SliverPersistentHeaderDelegate oldDelegate) {    return true;  }}