import 'package:flutter/material.dart';import 'package:flutter/services.dart';class MyHomePage extends StatefulWidget {  const MyHomePage({Key? key}) : super(key: key);  @override  State<MyHomePage> createState() => MyHomePageState();}class MyHomePageState extends State<MyHomePage> {  late final TextEditingController numController;  @override  void initState() {    super.initState();    numController = TextEditingController();  }  @override  void dispose() {    numController.dispose();    super.dispose();  }  @override  Widget build(BuildContext context) {    return Scaffold(        body: Column(      mainAxisAlignment: MainAxisAlignment.center,      children: [        Padding(          padding: const EdgeInsets.all(50.0),          child: TextFormField(            controller: numController,            keyboardType: TextInputType.number,            inputFormatters: <TextInputFormatter>[FilteringTextInputFormatter.digitsOnly],            decoration: const InputDecoration(                filled: true, labelText: 'Number', fillColor: Colors.white10, border: OutlineInputBorder()),          ),        ),        TextButton(            onPressed: () {              if (numController.text.isNotEmpty) {                Navigator.pushNamed(context, '/num', arguments: {"number": numController.text.toString()});              }            },            child: const Text('Отримати факт')),        TextButton(            onPressed: () {              Navigator.pushNamed(context, '/random');            },            child: const Text('Отримати факт про випадкове число')),        const Card(),      ],    ));  }}